'use strict';

module.exports = function(url, fighter, cookieJar) {
  var cj = fighter || cookieJar;
  
  if (typeof cj === 'string') {
    this.url = url + fighter;
  } else {
    this.url = 'https://www.bestfightodds.com' + url;
  }
  this.method = 'GET';
  this.jar = cj;
  this.headers = {
    'Referrer': 'https://www.bestfightodds.com/',
    //Change User-Agent to change the type of browser and operating system seen by the website
    'User-Agent': 'Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/56.0.2924.87 Safari/537.36',
    'Connection': 'keep-alive'
  }
}