'use strict';

module.exports = {
  
  // Clean up the fighter's name before passing it to the URL
  parseName: function(name) {
    name = name.replace(' ', '+').replace('/', '');
    name = escape(name);
    return name;
  },

  // Check if argument is a function
  isFunction: function(inputFunction) {
    return typeof(inputfunction) === typeof(Function);
  },
  
  // Is number odd or even
  oddEven: function(num) {
    return num % 2;
  },
  
  // Parse the date
  parseDate: function(eventDate) {
    if (eventDate) {
      let eventMonth = eventDate.substring(0, 3);
      let eventDay = eventDate.match(/\s\d+/)[0].trim();
      let eventYear = eventDate.match(/\s\d+$/)[0].trim();
      eventDate = new Date(eventMonth + eventDay + ', ' + eventYear);
    }
    return eventDate;
  }
}
